<div class="modal fade" id="assoc-modal" tabindex="-1" role="dialog" aria-labelledby="assoc-select-modal"
     aria-hidden="true">
    <div class="modal-dialog" role="document">
        <div class="modal-content">
            <div class="modal-header">
                <h5 class="modal-title" id="assoc-select-modal">选择关联</h5>
                <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>
            <div class="modal-body">
                <div class="row">
                    <div class="col-12 justify-content-center">
                        <div class="form-group">
                            <label for="modal-assoc-form">选择数据源表单</label>
                            <select id="modal-assoc-form" class="form-control"></select>
                        </div>
                        <div class="form-group">
                            <label for="modal-assoc-column">选择数据源列</label>
                            <select id="modal-assoc-column" class="form-control"></select>
                        </div>
                    </div>
                </div>
            </div>
            <div class="modal-footer">
                <button type="button" class="btn btn-secondary" data-dismiss="modal">取消</button>
                <button type="button" class="btn btn-primary" id="save-assoc-select">保存</button>
            </div>
        </div>
